function calculate(){'use strict';var cost;var type=document.getElementById('type');var years=document.getElementById('years');var output=document.getElementById('output');if(years&&years.value){years=parseInt(years.value,10)}if(type&&type.value&&years&&(years>0)){switch(type.value){case'basic':cost=10.00;break;case'premium':cost=15.00;break;case'gold':cost=20.00;break;case'platinum':cost=25.00;break}if(years<2){cost*=years}else if(years<4){cost=((cost*years)*.90)}else if(years<5){cost=((cost*years)*.85)}else{cost=((cost*years)*.80)}if(output.textContent!=undefined){output.textContent=cost.toFixed(2)}else{output.innerText=cost.toFixed(2)}}else{document.getElementById('cost').value='Please enter valid values.'}return false}function init(){'use strict';document.getElementById('theForm').onsubmit=calculate}window.onload=init;